import { createSlice } from "@reduxjs/toolkit";
import { nanoid } from "@reduxjs/toolkit";
import { sub } from 'date-fns'

const initialState = [
  { id: '1', title: 'First Post!', content: 'Hello! First Post', 
  date: sub(new Date(), { minutes: 10 }).toISOString(),
  reactions: {
    thumbsUp: 0,
    hooray: 0,
    heart: 0,
    rocket: 0,
    eyes: 0,
  },
 },
  { id: '2', title: 'Second Post', content: 'More text! 2nd Post', date: sub(new Date(), { minutes: 5 }).toISOString() }
]

const postsSlice = createSlice({
  name: 'posts',
  initialState,
  reducers:{
    // reducer to add new created post
    postAdded: {
      reducer(state, action) {
        state.push(action.payload)
      },
      prepare(title, content, userId) {
        return {
          payload: {
            id: nanoid(),
            title,
            content,
            user: userId,
            date: new Date().toISOString(),
            reactions: {
              thumbsUp: 0,
              hooray: 0,
              heart: 0,
              rocket: 0,
              eyes: 0,
            },
          }
        }
      }
    },

    // other reducers here
    // reducer to update existing post for that we need action, payload and state also
    postUpdated (state, action){
      const {id, title, content} = action.payload
      const existingPost = state.find(post => post.id === id)
      if (existingPost){
        existingPost.title = title
        existingPost.content = content
      }
    },

    reactionAdded(state, action){
      const {postId, reaction} = action.payload
      const existingPost = state.find(post => post.id = postId)
      if (existingPost){
        existingPost.reaction[reaction]++
      }
    }

  }
})

export const { postAdded, postUpdated, reactionAdded } = postsSlice.actions
export default postsSlice.reducer;
