import { createSlice } from "@reduxjs/toolkit";

const initialState = [
  { id: '1', title: 'First Post!', content: 'Hello! First Post' },
  { id: '2', title: 'Second Post', content: 'More text! 2nd Post' }
]

const postsSlice = createSlice({
  name: 'posts',
  initialState,
  reducers:{}
})

export default postsSlice.reducer;
